import React from 'react';
import { ImageLoader } from './ImageLoader';
import { ImageProcessor } from './ImageProcessor';

export class State
{
    static appInstance : App;

    static setImageData(imageData : Uint8Array)
    {
        this.appInstance.setState({imageData: imageData});
    }
}

class App extends React.Component
{
    state = { imageData: [] };

    componentDidMount()
    {
        //provide App reference to State
        State.appInstance = this;
    }

    render()
    {
        return (
            <div>
                <ImageLoader />
                <ImageProcessor />
                {this.downloads.map((download, key) => 
                    <div key={key}>
                        <img src={download}></img>
                        <a href={download} download="image.bmp">{"image-" + key}</a>
                    </div>
                )}
            </div>
        );
    }
}

export default App;
