import React from 'react';
import { Colors } from './Colors';
import { Styles } from './Styles';

interface AnimationPreviewProps
{
    url : string,
    isLoading : boolean
}

export class AnimationPreview extends React.Component<AnimationPreviewProps>
{
    render()
    {
        let containerStyle : React.CSSProperties = 
        {
            margin: "16px",
            padding: "16px",
            background: Colors.background,
            borderWidth: 1,
            borderStyle: "solid",
            borderColor: Colors.border,
            display: "inline-block",
            verticalAlign: "top"
        };

        let downloadButton = this.props.isLoading || this.props.url == "" ? null : <a href={this.props.url}>Download</a>;
        let imagePreview = this.props.isLoading ? "Loading animation..." : <img style={Styles.imageStyle} src={this.props.url}/>;

        return (
            <div style={containerStyle}>
                <h1 style={Styles.h1Style}>Animation</h1>
                {imagePreview}
                {downloadButton}
            </div>
        );
    }
}
