import React from 'react';
import { State } from './App';
import { Colors } from './Colors';
import { Styles } from './Styles';
import ReactDOM from 'react-dom';

interface DownloadAreaProps
{
    downloads : string[]
}

export class DownloadArea extends React.Component<DownloadAreaProps>
{
    render()
    {
        let containerStyle : React.CSSProperties = 
        {
            padding: "16px",
            background: Colors.background,
            borderWidth: 1,
            borderStyle: "solid",
            borderColor: Colors.border,
        };

        let downloadContainerStyle : React.CSSProperties = 
        {
            display: "inline-block"
        };

        return (
            <div style={containerStyle}>
                <h1 style={Styles.h1Style}>Downloads</h1>
                {this.props.downloads.map((download, key) => 
                    <div key={key} style={downloadContainerStyle}>
                        <img className="downloadImg" src={download} style={Styles.imageStyle}></img>
                        <a href={download} download="image.bmp">{"image-" + key}</a>
                    </div>
                )}
                <button onClick={() => this.convertToGif()}>Convert to GIF</button>
            </div>
        );
    }

    convertToGif()
    {
        //first find the actual image size of the first frame
        var newImg = new Image();
        newImg.onload = () =>
        {
            let width = newImg.width;
            let height = newImg.height;

            //@ts-ignore
            let gif = new GIF(
            {
                workers: 2,
                quality: 10,
                width: width,
                height: height
            });

            let thisNode = ReactDOM.findDOMNode(this)! as Element;
            let imgElements = Array.from(thisNode.getElementsByClassName('downloadImg'));
            
            imgElements.forEach(img => 
            {
                gif.addFrame(img, {delay: 10});
            });
            
            gif.on('finished', function(blob : Blob) 
            {
                window.open(URL.createObjectURL(blob));
            });
            
            gif.render();
        }

        let thisNode = ReactDOM.findDOMNode(this)! as Element;
        let imgElements = Array.from(thisNode.getElementsByClassName('downloadImg'));
        newImg.src = (imgElements[0] as HTMLImageElement).src;
    }
}
