import React from 'react';
import { ImageLoader } from './ImageLoader';
import { ImageProcessor } from './ImageProcessor';

export class State
{
    static app : App;

    static setImageData(imageData : Uint8Array)
    {
        this.app.setState({imageData: imageData});
    }

    static addDownload(url : string)
    {
        let newDownloads = this.app.state.downloads;
        newDownloads.push(url);
        this.app.setState({ downloads: newDownloads });
    }
}

interface AppProps
{

}

interface AppState
{
    imageData : Uint8Array,
    downloads : string[]
}

class App extends React.Component<AppProps, AppState>
{
    state : AppState = { imageData: new Uint8Array(), downloads: []};

    componentDidMount()
    {
        //provide App reference to State
        State.app = this;
    }

    render()
    {
        return (
            <div>
                <ImageLoader />
                <ImageProcessor imageData={this.state.imageData} />
                {this.state.downloads.map((download, key) => 
                    <div key={key}>
                        <img src={download}></img>
                        <a href={download} download="image.bmp">{"image-" + key}</a>
                    </div>
                )}
            </div>
        );
    }
}

export default App;
