import React from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from '@testing-library/react';

class App extends React.Component
{
    render()
    {
        return (
            <div>
            <input ref="fileInput" type="file" id="files" name="file" onChange={() => this.loadImage()}/>
            </div>
        );
    }

    loadImage()
    {
        let fileInput = this.refs.fileInput as HTMLInputElement;
        let imageFile = fileInput.files![0];

        let fileReader = new FileReader();
        fileReader.readAsArrayBuffer(imageFile);

        fileReader.onloadend = function()
        {
            //get data from file
            let result = fileReader.result as ArrayBuffer;
            let rawData = new Int16Array(result);
            console.log(rawData);

            //decode data
            //@ts-ignore
            let decodedFile = alawmulaw.mulaw.decode(rawData);
            console.log(decodedFile);

            //encode data
            //@ts-ignore
            let encodedFile = alawmulaw.mulaw.encode(decodedFile);
            console.log(encodedFile);

            this.saveByteArray(encodedFile);
        }

        /*
        //check for the various File API support.
        if(!(window.File && window.FileReader && window.FileList && window.Blob))
        {
            alert('The File APIs are not fully supported in this browser.');
            return;
        }
        */



    }

    saveByteArray = () => {
        let a = document.createElement("a");
        document.body.appendChild(a);
        a.style.display = "none";
        return function (data : any, name : string) {
            var blob = new Blob(data, {type: "octet/stream"}),
                url = window.URL.createObjectURL(blob);
            a.href = url;
            a.download = name;
            a.click();
            window.URL.revokeObjectURL(url);
        };
    };
}

export default App;
