import React from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from '@testing-library/react';
import { compileFunction } from 'vm';

class App extends React.Component
{
    downloads : string[] = [];

    render()
    {
        return (
            <div>
                <input ref="fileInput" type="file" id="files" name="file" onChange={() => this.loadImage()}/>
                {this.downloads.map((download, key) => 
                    <div key={key}>
                        <img src={download}></img>
                        <a href={download} download="image.bmp">{"image-" + key}</a>
                    </div>
                )}
            </div>
        );
    }

    loadImage()
    {
        let fileInput = this.refs.fileInput as HTMLInputElement;
        let imageFile = fileInput.files![0];

        let fileReader = new FileReader();
        fileReader.readAsArrayBuffer(imageFile);

        fileReader.onloadend = () =>
        {
            //get data from file
            let result = fileReader.result as ArrayBuffer;
            let rawData = new Uint8Array(result);
            console.log(rawData);

            //decode data
            //@ts-ignore
            let decodedFile = alawmulaw.mulaw.decode(rawData);
            console.log(decodedFile);

            //process data
            let processedData = this.bufferProcess(decodedFile);

            //encode data
            //@ts-ignore
            let encodedFile = alawmulaw.mulaw.encode(processedData);
            console.log(encodedFile);

            this.saveByteArray(encodedFile);
        }
    }

    saveByteArray(data : any)
    {
        let blob = new Blob([data], {type: "image/bmp"});
        let url = window.URL.createObjectURL(blob);

        this.downloads.push(url);

        this.setState({});
    }

    bufferProcess(buffer : any)
    {
        let frequency = 0.01;
        let processedBuffer = [];
        for (let i = 0; i < buffer.length; i++) 
        {
            let angle = i * frequency;
            let amp = Math.sin(angle);

            const sample = buffer[i];
            let processedSample = sample * amp;
            processedBuffer.push(processedSample);
        }

        return processedBuffer;
    }
}

export default App;
