import React from 'react';
import { State } from './App';
import { Colors } from './Colors';
import { Styles } from './Styles';

interface ImageProcessorProps
{
    imageData : Uint8Array
}

export class ImageProcessor extends React.Component<ImageProcessorProps>
{
    state = { previewUrl: "" };

    render()
    {
        let containerStyle : React.CSSProperties = 
        {
            padding: "16px",
            background: Colors.background,
            borderWidth: 1,
            borderStyle: "solid",
            borderColor: Colors.border,
            display: "inline-block"
        };

        let innerContainerStyle : React.CSSProperties = 
        {
            paddingBottom: "16px"
        };

        return (
            <div style={containerStyle}>
                <h1 style={Styles.h1Style}>Process Image</h1>
                <label htmlFor="startFreq">Start Frequency</label> <input id="startFreq" ref="startFreqInput" type="number" defaultValue="0.01"/>
                <br />
                <label htmlFor="endFreq">End Frequency</label> <input id="endFreq" ref="endFreqInput" type="number" defaultValue="0.01"/>
                <br />
                <br />
                <label htmlFor="startPhase">Start Phase</label> <input id="startPhase" ref="startPhaseInput" type="number" defaultValue="0"/>
                <br />
                <label htmlFor="endPhase">End Phase</label> <input id="endPhase" ref="endPhaseInput" type="number" defaultValue="0"/>
                <br />
                <br />
                <button onClick={() => this.processFrame()}>Process Frame</button>
                <button onClick={() => this.processAnimation()}>Process Animation</button>
            </div>
        );
    }

    processFrame()
    {
        //get data from file
        let rawData = this.props.imageData;
        
        //decode data
        //@ts-ignore
        let decodedFile = alawmulaw.mulaw.decode(rawData);

        //process data
        let processedData = this.bufferProcess(decodedFile);

        //encode data
        //@ts-ignore
        let encodedFile = alawmulaw.mulaw.encode(processedData);
        
        this.saveByteArray(encodedFile);
    }

    processAnimation()
    {
        this.processFrame();
    }

    bufferProcess(buffer : any)
    {
        let headerLength = 4000;

        let frequencyInput = this.refs.frequencyInput as HTMLInputElement;
        let frequency = parseFloat(frequencyInput.value);

        let processedBuffer = [];
        for (let i = 0; i < buffer.length; i++) 
        {
            const sample = buffer[i];

            if(i < headerLength)
            {
                processedBuffer.push(sample);
                continue;
            }

            let angle = i * frequency;
            let amp = Math.sin(angle);

            let processedSample = sample * amp;
            processedBuffer.push(processedSample);
        }

        return processedBuffer;
    }

    saveByteArray(data : any)
    {
        let blob = new Blob([data], {type: "image/bmp"});
        let url = window.URL.createObjectURL(blob);

        State.addDownload(url);
    }
}
